## ----exec-calib-green---------------------------------------------------------
calibration = readRDS("data/wisla_calib.rds")
aux <- calibration[1:4,] %>% 
  select(alpha_P, alpha_L, sd_coeff, NSE, mNSE, rNSE, KGE, PBIAS..) %>%
  rename("PBIAS %" = PBIAS..)
rownames(aux) <- paste0("s", 1:4)
aux


